import React, { Component } from 'react';

const Button = (props) => {
  return (
    <button className="cont-onboarding" onClick={props.Listener}>
      <span className={props.Classes}></span>
    </button>
  );
}

const defCheckins =
  [
    [
      {
        q: 'Have you made time to do something you love?',
        type: 'yes/no'
      },
      {
        q: 'How loving were you towards yourself?',
        type: 'range'
      },
      {
        q: 'What are you grateful for?',
        type: 'text'
      }
    ],
    [
      {
        q: 'Have you taken a deep breath today?',
        type: 'yes/no'
      },
      {
        q: 'Have you listened to relaxing music?',
        type: 'yes/no'
      },
      {
        q: 'Did you spend time in nature?',
        type: 'yes/no'
      }
    ],
    [
      {
        q: 'Have you reached out to friends or family today?',
        type: 'yes/no'
      },
      {
        q: 'Have you done something for someone else?',
        type: 'yes/no'
      }
    ],
    [
      {
        q: 'How overwhelmed do you feel?',
        type: 'range'
      },
      {
        q: 'Did you feel anxious about something?',
        type: 'yes/no'
      }
    ],
    [
      {
        q: 'How hydrated are you?',
        type: 'range'
      },
      {
        q: 'Have you stretched and moved your body?',
        type: 'yes/no'
      },
      {
        q: 'How was your sleep?',
        type: 'range'
      },
    ],
  ];

const INITIAL_STATE = {
  categories: [
    'mindful',
    'relaxation',
    'social',
    'stress',
    'physical'
  ],
  showOptions: [
    false,
    false,
    false,
    false,
    false,
  ],
  selectedCheckins: [
    [false, false, false],
    [false, false, false],
    [false, false],
    [false, false],
    [false, false, false],
  ]
};

class OnboardingCategories extends React.Component {
  constructor(props) {
    super(props);
    this.state = INITIAL_STATE;
  }


  changeSelected = (i, j) => {
    let selected = this.state.selectedCheckins;
    selected[i][j] = !selected[i][j];
    this.setState({ selectedCheckins: selected })
  }

  changeOptions = (i) => {
    let arr = this.state.showOptions;
    arr[i] = !arr[i];
    this.setState({ showOptions: arr })
  }

  onSubmit = () => {
    let checkins = [], s = this.state.selectedCheckins;
    for (var i = 0; i < s.length; i++) {
      for (var j = 0; j < s[i].length; j++) {
        if (s[i][j] === true) {
          checkins.push(this.state.defCheckins[i][j])
        }
      }
    }
    this.props.setCheckins(checkins)
    this.props.firebase
      .user(this.props.uid)
      .update({
        checkins: checkins
      });
  }


  render() {

    let { categories, showOptions, selectedCheckins } = this.state;


    let checkins = [];

    for (var i = 0; i < showOptions.length; i++) {

      let addEl;

      if (showOptions[i] === true) {
        var arr = defCheckins[i];
        var defCh = [];
        for (var j = 0; j < arr.length; j++) {
          let className = '';
          let item = arr[j];

          if (selectedCheckins[i][j] === true) {
            className = 'ob-cat-opt selected'
          } else {
            className = 'ob-cat-opt'
          }

          let el = null;
          if (item.type == 'text') {
            el = (
              <span className="jam checkicon jam-align-justify"></span>
            );
          }
          else if (item.type == 'yes/no') {
            el = (
              <span className="jam checkicon jam-brightness"></span>
            );
          } else {
            el = (
              <span className="jam checkicon jam-star"></span>
            );
          }

          defCh.push(<button className={className} onClick={() => this.changeSelected(i, j)}>
            {el}
            {item.q}
          </button>);
        }

        addEl = (
          <div className="ob-cat">
            <button >
              {categories[i]}
            </button>

            {defCh}

          </div>
        );
      }
      else {
        addEl = (
          <button onClick={() => this.changeOptions(i)}>
            {categories[i]}
          </button>
        );
      }
      checkins.push(addEl);
    }
    return (
      <div className="categories">
        {checkins}

        <Button
          Listener={this.onSubmit}
          Classes="jam jam-check"
        />
      </div>

    );
  }
};

export default OnboardingCategories;
