import React from 'react';
import AddCheckin from '../Network/AddCheckin';
import $ from 'jquery';
import Avatar from '../Misc/Avatar';
import UploadProfilePicture from '../Misc/UploadProfilePicture';
import CheckInRow from '../Network/CheckinRow';
import Dropdown from '../Misc/Dropdown';
import autosize from 'autosize';
import StaticUserData from '../../data/StaticUserData'
import Settings from './Settings/Settings'
import CheckinModule from './CheckinModule'


const EditInput = (props) => {
  return <div className="input-container">
    <span className="input-label">
      {props.title}
    </span>
    <input
      name={props.name}
      type="text"
      className={"profile-" + props.name}
      placeholder={props.placeholder}
      value={props.value}
      onChange={this.onChangeUserInfo}>
    </input>
  </div>
}

class UserTab extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      editMode: false,
      checkins: this.props.checkins,
      name: this.props.name,
      bio: this.props.bio,
      location: this.props.location,
      pronouns: this.props.pronouns,
      checkinFreq: this.props.checkinFreq,
      addMode: false,
      PpfURL: this.props.PpfURL,
      madeChanges: false,
      showSettings: false
    };
  }

  componentDidMount() {
    autosize($('textarea'));
  }

  setEditMode = (bool) => {
    this.setState({ editMode: bool });

    if (bool === false) {
      if (this.state.madeChanges == true) {

        let { name, bio, location, pronouns } = this.state;

        this.props.setUserInfo(name, bio, location, pronouns);

        this.props.firebase
          .user(this.props.uid)
          .update({
            name: name,
            bio: bio == null ? null : bio,
            location: location == null ? null : location,
            pronouns: pronouns == null ? null : pronouns,
            checkinFreq: this.state.checkinFreq
          });

        this.setMadeChanges(false)
      }
    }
  };

  setMadeChanges = (bool) => this.setState({ madeChanges: bool });

  setAddMode = (bool) => this.setState({ addMode: bool });

  setShowSettings = (bool) => this.setState({ showSettings: bool });

  addCheckin = (obj) => {
    let arr = this.state.checkins;
    arr.push(obj);
    this.setState({ checkins: arr });
    this.props.setCheckins(arr);

    this.props.firebase
      .user(this.props.uid)
      .update({
        checkins: arr
      });
  }

  removeCheckinAt = (index) => {
    let arr = this.state.checkins;
    arr.splice(index, 1);
    this.setState({ checkins: arr });
    this.props.setCheckins(arr);

    this.props.firebase
      .user(this.props.uid)
      .update({
        checkins: arr
      });
  }

  uploadPpf = (event) => {
    let firstFile = event.target.files[0] // upload the first file only
    let task = this.props.firebase.photos(this.props.uid).put(firstFile);

    task.on('state_changed', (snapshot) => {
      let progress = (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
    }, (error) => {
      // Handle unsuccessful uploads
    }, () => {
      // Handle successful uploads on complete
      task.snapshot.ref.getDownloadURL().then((downloadURL) => {

        this.props.firebase
          .user(this.props.uid)
          .update({
            PpfURL: downloadURL
          });

        this.setState({ PpfURL: downloadURL });
        this.props.setPpfURL(downloadURL);
      });
    });
  }

  onChangeUserInfo = event => {
    this.setState({ [event.target.name]: event.target.value });
    this.setMadeChanges(true)
  };

  setPronouns = (pronoun) => {
    if (pronoun == StaticUserData.NULL_KEY) {
      this.setState({ pronouns: null })
      return;
    }

    this.setState({ pronouns: pronoun })
    this.setMadeChanges(true)
  }

  setCheckinFreq = (triggeredIndex) => {
    let tempFreq = this.state.checkinFreq;

    tempFreq[triggeredIndex] = !tempFreq[triggeredIndex];

    this.setState({ checkinFreq: tempFreq })
    this.setMadeChanges(true)
  }

  render() {
    if (this.state.showSettings == true) {
      return <Settings
        firebase={this.props.firebase}
        uid={this.props.uid}
        removeUser={this.props.removeUser}
        updateUserData={this.props.updateUserData}
        goBack={() => this.setShowSettings(false)}
      />
    }

    const { pronouns } = this.state;

    let cornerButtons,
      pic,
      name,
      bioEl,
      locationEl,
      pronounsEl,
      checkins,
      addQ,
      freq;


    if (this.state.editMode) {
      cornerButtons = (
        <div className="header">
          <button className="edit done" onClick={() => this.setEditMode(false)}>Save</button>
        </div>
      );
      pic = <UploadProfilePicture uploadPpf={this.uploadPpf} PpfURL={this.state.PpfURL}/>;

      name = (
        <EditInput
          title='Full name'
          name='name'
          placeholder='Full name'
          value={this.state.name}
        />
      );

      bioEl = (
        <div className="input-container">
          <span className="input-label">
            About me
        </span>
          <textarea
            name="bio"
            rows="2"
            className="profile-bio"
            placeholder="Write about yourself."
            value={this.state.bio}
            onChange={this.onChangeUserInfo}>
          </textarea>
        </div>

      );

      locationEl = (
        <EditInput
          title='Location'
          name='location'
          placeholder='Where are you?'
          value={this.state.location}
        />
      );

      pronounsEl = (
        <div className="input-container">
          <span className="input-label">
            Pronouns
          </span>
          <Dropdown
            options={[
              StaticUserData.allPronouns[StaticUserData.NULL_KEY],
              StaticUserData.allPronouns[StaticUserData.FEMALE_PRONOUN],
              StaticUserData.allPronouns[StaticUserData.MALE_PRONOUN],
              StaticUserData.allPronouns[StaticUserData.NON_PRONOUN]
            ]}
            onChange={this.setPronouns}
            selected={StaticUserData.allPronouns[pronouns != null ? pronouns : StaticUserData.NULL_KEY].index}
          ></Dropdown>
        </div>
      );

    }
    else {
      cornerButtons = (
        <div className="header">
          <button className="settings" onClick={() => this.setShowSettings(true)}>
            <span className="jam jam-cog"></span>
          </button>
          <button className="edit" onClick={() => this.setEditMode(true)}>
            <span className="jam jam-pencil"></span>
          </button>
        </div>
      );

      pic = (
        <Avatar PpfURL={this.state.PpfURL} />
      );

      name = (
        <div className="profile-name">{this.state.name}</div>
      );

      if (this.state.bio != null) {
        bioEl = <p className="profile-bio">
          {this.state.bio ? this.state.bio : null}
        </p>
      } else {
        bioEl = null;
      }

      if (this.state.location != null) {
        locationEl = <div className="profile-location">
          <span className="jam checkicon jam-map-marker"></span>
          {this.state.location}
        </div>;
      } else {
        locationEl = null;
      }

      if (pronouns != null) {
        pronounsEl = <div className="profile-pronouns">
          <span className={"jam checkicon jam-" + StaticUserData.allPronouns[pronouns].icon}></span>
          {' ' + pronouns}
        </div>;
      }
      else {
        pronounsEl = null;
      }
    }
    checkins = {};
    for (let i = 0; i < this.state.checkins.length; i++) {
      let checkin = this.state.checkins[i];
      console.log(checkin.q + ' .. category: ' + checkin.category);
      if (checkin.category == null || checkin.category == '') {
        checkin.category = StaticUserData.getMiscCategory();
      }

      if (!(checkin.category in checkins)) {
        checkins[checkin.category] = [];
      }

      checkins[checkin.category].push(
        <CheckInRow
          checkin={checkin}
          removeCheckinAt={this.removeCheckinAt}
          index={i}
          trash={this.state.editMode}
        />
      );
    }

    let checkinsEl = [];
    for (let category in checkins) {
      let el = <div className={"qcat " + category}>
        <h2>{category}</h2>
        <ul className="your-checkins">
          {checkins[category]}
        </ul>
      </div>;

      if (category == StaticUserData.getMiscCategory()) {
        console.log(category + ' is misc ');
        checkinsEl.push(el);
      } else {
        checkinsEl.unshift(el);
      }
    }

    let style = {};
    if (this.state.editMode) {
      style = {
        display: 'contents'
      }
    }

    return (
      <section className="user">
        {cornerButtons}
        {pic}
        {name}
        {bioEl}
        <div className="profile-footer" style={style}>
          {locationEl}
          {pronounsEl}
        </div>

        <CheckinModule />

      </section>
    );
  }
}


export default UserTab;
