import React, { Component } from 'react';

const INITIAL_STATE = {
  q: '',
  type: 'text',
  dropdown: false
};

class AddCheckin extends React.Component {
  constructor(props) {
    super(props);
    this.state = INITIAL_STATE;
  }

  onChange = event => {
    this.setState({ [event.target.name]: event.target.value });
  };

  setType = (type) => {
    this.setState({ type: type });
  }

  onSubmit = () => {
    this.props.setAddMode(false);
    this.props.addCheckin({
      q: this.state.q,
      type: this.state.type
    });
  }

  setDropdown = (bool) => {
    this.setState({ dropdown: bool });
  }

  onCancel = () => {
    this.props.setAddMode(false);
  }

  render() {
    const { q, dropdown, type } = this.state;

    let dropdownEl;

    if (dropdown) {
      dropdownEl = (

        <ul>
          <li><button onClick={() => {
            setType('text');
            setDropdown(false);
            }}>
            <span className="jam jam-align-justify"></span> Text
        </button>
          </li>
          <li><button onClick={() => {
            setType('yes/no');
            setDropdown(false)
            }}>
            <span className="jam jam-brightness"></span> Yes/No
          </button>
          </li>
          <li><button onClick={() => {
            setType('scale');
            setDropdown(false)
            }}>
            <span className="jam jam-star"></span> Scale</button>
          </li>

        </ul>
      );
    } else {
      if (type == 'text') {
        dropdownEl = (
          <ul>
            <li><button onClick={() => setDropdown(true)}>
              <span className="jam jam-align-justify"></span> Text
            </button>
            </li>
          </ul>
        );
      }
      else if (type == 'yes/no') {
        dropdownEl = (
          <ul>
            <li><button onClick={() => setDropdown(true)}>
              <span className="jam jam-brightness"></span> Yes/No
          </button>
            </li>
          </ul>
        );
      } else {
        dropdownEl = (
          <ul>
            <li><button onClick={() => setDropdown(true)}>
              <span className="jam jam-star"></span> Scale</button>
            </li>
          </ul>
        );
      }
    }

    return (
      <div className="add-panel">
        <h1>Add Check-in</h1>
        <textarea
          id="check-in"
          name="q"
          placeholder="What's your check-in?"
          value={q}
          onChange={this.onChange}>
        </textarea>
        <h3>Check-in type</h3>
        {dropdownEl}
        <div className="yesno">
          <button className="yes" onClick={this.onSubmit}>
            <span className="jam jam-check" style={{ color: 'white' }}></span></button>
          <button className="no" onClick={this.onCancel}>
            <span className="jam jam-close" style={{ color: '#8A8184' }}> </span></button>
        </div>
      </div>
    );
  }
}


export default AddCheckin;
